//import { getCurrencyRates } from "../libs/currency-data";
const parallel = require("map-parallel");
var async = require("async");
const os = require("os");

export async function calculateMaximumProfit(body) {
    const promise = new Promise(function(resolve, reject) {
        var result = generateCurrencyGraph(body);
        if(result!=null){
            resolve(result);
        } else{
            reject("Maximum profit not calculated");
        }
    });
    return promise;
}


var min = -9999999;

//This is like a virtual class
/*var edge = {
    id: 0,
    src_bank: 0,
    dest_bank: 0,
    rate: 0,
    extra_cost: 0
};*/

var graph = {
    edges:[],
    banks: 0,
    rates: 0,
    create: function(){
        for (var i = 0; i < this.rates; i++) {
            // Initialize the graph with empty nodes
            var new_edge = {src_bank:0,dest_bank:0};
            this.edges.push(new_edge);
        }
        return this;
    },
};

function calculateMoneyWithCurrencyRate(local_currency, rate, extra_cost){
    var result = local_currency * rate;
    result = result*((100-extra_cost)/100);
    return result;
}

function Forex(graph, src_bank)
{
    var V = graph.banks;
    var E = graph.rates;
    var dist = [];
    var bridges = [];

    // Step 1: Initialize distances from src to all other vertices as INFINITE negative
    for (var z = 0; z < V; ++z){
        dist[z] = min;
        bridges[z] = [src_bank];
    }

    //Start with 1 dollar
    dist[src_bank] = 1;

    for (var i = 1; i < 10; ++i) {
        for (var j = 0; j < E; ++j) {
            var u = graph.edges[j].src_bank;
            var v = graph.edges[j].dest_bank;
            var extra_cost = graph.edges[j].extra_cost;
            var rate = graph.edges[j].rate;
            var result = calculateMoneyWithCurrencyRate(dist[u],rate,extra_cost);
            if (dist[u] != min && result > dist[v]){
                dist[v] = result;
                var new_route = bridges[u].slice(0);
                bridges[v] = new_route;
                bridges[v].push(v);
            }
        }
    }
    printArr(dist, bridges, V);
    return {dist, bridges};
}

// A utility function used to print the solution
function printArr(dist, bridges, V)
{
    console.log("Vertex Distance from Source");
    for (var i = 0; i < V; ++i){
        console.log(i + "\t\t" + dist[i]);
        console.log(bridges[i]);
        console.log("--------------");
    }

}

function buildGraph(link,currency,position){
    console.log("I tried 3");
    link.id = position;
    link.src_bank = currency[position].src_id;
    link.dest_bank = currency[position].dest_id;
    link.rate = currency[position].rate; //TODO: call API here
    link.extra_cost = 0;

    return link;
}

function generateCurrencyGraph(data){
    var new_graph = {edges:[],banks:data.total_currencies,rates:data.total_rates};
    new_graph = graph.create.call(new_graph);
    console.log("I tried 1");
    console.log(new_graph.edges);
    parallel.map(new_graph.edges, os.cpus().length, (item, base) => {
        console.log("I tried 2");
        return buildGraph(item,data.currencies,new_graph.edges.indexOf(item));
    }, []).then((result) => {
        console.log(result);
        console.log("----------------");
    });
    /*for(var count = 0; count < data.total_rates; count++){
        new_graph.edges[count].id = count;
        new_graph.edges[count].src_bank = data.currencies[count].src_id;
        new_graph.edges[count].dest_bank = data.currencies[count].dest_id;
        new_graph.edges[count].rate = data.currencies[count].rate;
        new_graph.edges[count].extra_cost = 0;
    }*/
    return Forex(new_graph,0);
}
