const AWS = require('aws-sdk');
export async function getCurrencyRates(source, dest) {
    // --------------- TCI Service Descriptor -----------------------
    // define target API as service
    const service = new AWS.Service({
        // TIBCO Cloud Integration base API URL,
        // can be even more secured using TIBCO Mashery.
        //endpoint: "https://api.exchangeratesapi.io/latest?base="+source+"&symbols="+dest,
        endpoint: "https://api.exchangeratesapi.io/latest?base=SGD&symbols=MYR",
        convertResponseTypes: false,

        // TCI Flogo API REST
        apiConfig: {
            metadata: {
                protocol: 'rest-json' // API is JSON-based
            },
            operations: {

                // TCI Flogo custom Endpoint
                // get Data by a record id
                getData: {
                    http: {
                        method: 'GET',
                        requestUri: ''
                    },
                    input: {
                        type: 'structure',
                        required: [ ]
                    }
                }
            }
        }
    });

    const promise = new Promise(function(resolve, reject) {
        service.isGlobalEndpoint = true;
        console.log("https://api.exchangeratesapi.io/latest?base="+source+"&symbols="+dest);
        service.getData({}, (err, data) => {
            if (err) {
                console.error(':>> operation error:', err);
                reject(err);
            }
            resolve(data);
        });
    });
    return promise;
}
