import { success, failure } from "../libs/response-lib";
import { calculateMaximumProfit } from "../libs/bellman-ford-lib";
import { currency_data } from "../resources/currency.json";
export async function main(event, content, callback){
    //const data = JSON.parse(event.body);


    try{
        //TODO: call currency API (set USD as the main currency)
        //TODO: attach original value of the currency exchange

        //var data = await getCurrencyRates("USD", "AUD");
        //console.log(data);
        //data = await getCurrencyRates("SGD", "MYR");
        //console.log(data);
        //console.log("-----------------------------");
        var body = {};
        body.total_currencies = 3;
        body.total_rates = 6;
        body.currencies = currency_data;
        //console.log(body);
        var result = await calculateMaximumProfit(body);
        console.log(result);
        return success(result);
    } catch (e){
        console.log(e);
        return failure({status:false});
    }
}
