import React, { Component } from "react";
import logo from './logo.jpg';
import './App.css';
import { bounce } from 'react-animations';
import styled, { keyframes } from "styled-components";
import { API } from "aws-amplify";

const Bounce = styled.div`
  animation: infinite 5s ${keyframes`${bounce}`};
`;

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            forex: null,
            eur_usd: 0,
            eur_jpy: 0,
            usd_jpy: 0,
            usd_eur: 0,
            jpy_usd: 0,
            jpy_eur: 0,
        };
    }

    async componentDidMount() {
        try {
            const forex_object = await this.getForex();
            console.log(forex_object);
            this.setState({
                forex:forex_object.banks,
                eur_usd: forex_object.edges[0].rate,
                eur_jpy: forex_object.edges[1].rate,
                usd_jpy: forex_object.edges[2].rate,
                usd_eur: forex_object.edges[3].rate,
                jpy_usd: forex_object.edges[4].rate,
                jpy_eur: forex_object.edges[5].rate,
            });
        } catch (e) {
            alert(e);
        }
    }

    getForex() {
        return API.get("forex", "forex");
    }

    renderForex(){
        return (
            <p>
                {
                    this.state.forex
                }
            </p>
        );
    }

    render() {
        return (
            <div className="App">
                <header className="App-header">
                    <Bounce><img src={logo} className="App-logo" alt="logo"/></Bounce>
                    <p>
                        Let's find the way to maximize your money
                    </p>
                    {this.renderForex()}
                    <p>1 EUR = {this.state.eur_usd} USD</p>
                    <p>1 EUR = {this.state.eur_jpy} JPY</p>
                    <p>1 USD = {this.state.usd_jpy} JPY</p>
                    <p>1 USD = {this.state.usd_eur} EUR</p>
                    <p>1 JPY = {this.state.jpy_usd} USD</p>
                    <p>1 JPY = {this.state.jpy_eur} EUR</p>
                </header>
            </div>
        );
    }
}

export default App;
