import React, { Component } from "react";
import logo from './logo.jpg';
import './App.css';
import { bounce } from 'react-animations';
import styled, { keyframes } from "styled-components";
import { API } from "aws-amplify";

const Bounce = styled.div`
  animation: infinite 5s ${keyframes`${bounce}`};
`;

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            forex: null
        };
    }

    async componentDidMount() {
        try {
            const forex = await this.forexes();
            this.setState({ forex });
        } catch (e) {
            alert(e);
        }

        this.setState({ isLoading: false });
    }

    forexes(){
        this.getForex().then(function(value) {
            return value;
        });
    }

    getForex() {
        var result = API.get("forex", "forex");
        return result
    }

    renderForex(){
        return (
            <p>
                {
                    new Promise( (resolve, reject) => {
                        resolve (this.generateForexTags());
                    })
                }
            </p>
        );
    }

    render() {
        return (
            <div className="App">
                <header className="App-header">
                    <Bounce><img src={logo} className="App-logo" alt="logo"/></Bounce>
                    <p>
                        Let's find the way to maximize your money
                    </p>
                    {this.renderForex()}
                </header>
            </div>
        );
    }
}

export default App;
