import React, { Component } from "react";
import logo from './logo.jpg';
import './App.css';
import { bounce } from 'react-animations';
import styled, { keyframes } from "styled-components";
import { API } from "aws-amplify";

const Bounce = styled.div`
  animation: infinite 5s ${keyframes`${bounce}`};
`;

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            forex: null
        };
    }

    async componentDidMount() {
        try {
            var forex = this.state.forex;
            this.getForex().then(function(value) {
                console.log(value);
                forex = value;
            });
            console.log(this.state.forex);
        } catch (e) {
            console.log(e);
            alert(e);
        }

        this.setState({ isLoading: false });
    }

    getForex() {
        var result = API.get("forex", "forex");
        console.log(result);
        return result
    }

    render() {
        return (
            <div className="App">
                <header className="App-header">
                    <Bounce><img src={logo} className="App-logo" alt="logo"/></Bounce>
                    <p>
                        Let's find the way to maximize your money
                    </p>
                    <p>{this.state.forex}</p>
                </header>
            </div>
        );
    }
}

export default App;
